import React from 'react';
import './stylesheets/styles.scss';
import beams from './images/beams.jpg';
import monsoon from './images/monsoon.jpg';
import move from './images/move.jpg';
import TextBlock from './components/TextBlock';
import Section from './components/Section';

class App extends React.Component {
  constructor(){
    super();
    this.state = {
      stack: false
    }
  }
  componentDidMount() {
    window.addEventListener('resize', () => {
        this.setState({
            stack: window.innerWidth < 1200
        });
    }, false);
  };

  render(){
    const className = this.state.stack ? 'column' : 'row';
    const className2 = this.state.stack ? '' : 'column column--1/2';

    let sectionMonsoon;

    if (!this.state.stack){
      sectionMonsoon = (
      <React.Fragment>
        <div className="column column--1/2">
          <img src={monsoon} />
        </div>
        <TextBlock title="MONSOON III" stack={this.state.stack} />
        </React.Fragment>
      )
    }
    else{
      sectionMonsoon = (
        <React.Fragment>
          <TextBlock title="MONSOON III" stack={this.state.stack} />
          <img src={monsoon} />
        </React.Fragment>
      )
    }

    return (
      <div>

        <section>
          <Section title="MONSOON III" stack={this.state.stack} name="monsoon"/>
        </section>

        <section className="beams">
          <div className={className}>
            <TextBlock title="BEAMS" stack={this.state.stack} />
            <div className={className2}>
              <img src={beams} />
            </div>
          </div>

          <Section title="Move2" stack={this.state.stack} name="move"/>
        </section>
      </div>
    );
  }
}

export default App;
