import React from 'react';
import '../stylesheets/styles.scss';

class Section extends React.Component{
  constructor(props){
    super(props);
  }

  render(){
    const className = this.props.stack ? 'column' : 'row';
    let section;

    if (!this.props.stack){
      section = (
      <React.Fragment>
        <div className="column column--1/2">
        <img src={require(`../images/${this.props.image.toLowerCase()}.jpg`)}/>
        </div>
        <TextBlock title={this.props.title} stack={this.props.stack} />
        </React.Fragment>
      )
    }
    else{
      section = (
        <React.Fragment>
          <TextBlock title={this.props.title} stack={this.props.stack} />
          <img src={require(`../images/${this.props.image.toLowerCase()}.jpg`)}/>
        </React.Fragment>
      )
    }

    return(
        <section className="monsoon">
        <div className="row">
        <div className={className}>
        </div>
        </div>
      </section>
    )
  }

}

export default Section;
