import React from 'react';
import './stylesheets/styles.scss';
import beams from './images/beams.jpg';
import monsoon from './images/monsoon.jpg';
import move from './images/move.jpg';
import TextBlock from './components/TextBlock';
import Section from './components/TextBlock';

class App extends React.Component {
  constructor(){
    super();
    this.state = {
      stack: false
    }
  }
  componentDidMount() {
    window.addEventListener('resize', () => {
        this.setState({
            stack: window.innerWidth < 1200
        });
    }, false);
  };

  render(){
    const className = this.state.stack ? 'column' : 'row';
    const className2 = this.state.stack ? '' : 'column column--1/2';

    let sectionMonsoon;

    if (!this.state.stack){
      sectionMonsoon = (
      <React.Fragment>
        <div className="column column--1/2">
          <img src={monsoon} />
        </div>
        <TextBlock title="MONSOON III" stack={this.state.stack} />
        </React.Fragment>
      )
    }
    else{
      sectionMonsoon = (
        <React.Fragment>
          <TextBlock title="MONSOON III" stack={this.state.stack} />
          <img src={monsoon} />
        </React.Fragment>
      )
    }

    return (
      <div>

        {/* <section className="monsoon">
          <div className={className}>
            {sectionMonsoon}
          </div>
        </section> */}


        <section className="beams">
          <div className={className}>
            <TextBlock title="BEAMS" stack={this.state.stack} />
            <div className={className2}>
              <img src={beams} />
            </div>
          </div>

          <div className="row">
            <div className="column column--1/2">
            <img src={move} />
            </div>
            <div className="column column--1/2">
            <h1>Move 2</h1>
            <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.</p>
            </div>
          </div>
        </section>
      </div>
    );
  }
}

export default App;
